import React, { useEffect, useState } from "react";
import { google_signin } from "firebase";
import { db, auth } from "firebase"; // Update the import path
import { collection, query, where, onSnapshot } from "firebase/compat/firestore"; // Update the import path for Firestore

const Home = () => {

    const [users, setUsers] = useState([]);
    useEffect(() => {
        // Query the database
        const usersRef = collection(db, users);
        const q = query(usersRef, where('uid', 'not-in', [auth.currentUser.uid]));
        const unsub = onSnapshot(q, querySnapshot => {
            let users = [];
            querySnapshot.forEach(doc => {
                users.push(doc.data());
            });
            setUsers(users);
        })
        return () => unsub();
    }, [])
    console.log(users);
    return (
        <div className="App">
            <p>Home page</p>
            <button onClick={google_signin} type="button" class="login-with-google-btn" >
                Sign in with Google
            </button>
      </div>
    )
};

export default Home;
