import React, { useEffect } from 'react';
import { UserAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import { storeUserData } from '../components/StoreUser';

const Login = () => {

    const { googleSignIn, user } = UserAuth();
    const navigate = useNavigate();

    const handleGoogleSignIn = async () => {
        try {
            await googleSignIn();
            // After successful sign-in, store user data
            if (user != null) {
            //const { uid, email, displayName } = user;
            storeUserData(user.uid, user.email, user.displayName);
            console.log(user.uid);
            }
        } catch (error) {
            console.log(error);
        }
    };
    
    useEffect(() => {
        if(user != null) {
            navigate('/account');
        }
    }, [user, navigate]);


    return (
        <div className="App">

            <button onClick={handleGoogleSignIn} type="button" class="login-with-google-btn" >
                Sign in with Google
            </button>
        </div>

    );
};

export default Login;
