import { db } from '../firebase'; // Import the Firestore instance from your firebase.js file

// Function to add user data to Firestore
export const addUserToFirestore = async (user) => {
  try {
    if (!user) {
      console.error('User is not authenticated.');
      return;
    }

    const userRef = db.collection('users').doc(user.uid);

    // Check if the user document already exists, and update it if needed
    const docSnapshot = await userRef.get();

    if (!docSnapshot.exists) {
      // If the user document doesn't exist, create a new one
      await userRef.set({
        displayName: user.displayName,
        email: user.email,
        uid: user.uid,
      });
    } else {
      // If the user document already exists, you can choose to update it here if needed
      // For example, you might want to update the user's display name or email
    }

    console.log('User data added to Firestore.');
  } catch (error) {
    console.error('Error adding user data to Firestore:', error);
  }
};
