import React, { useRef, useState } from "react";
import { auth, db } from "../firebase";
import { addDoc, collection, idField, serverTimestamp, orderBy } from "firebase/firestore";
import { useCollectionData } from 'react-firebase-hooks/firestore';

const Chatroom = () => {
    const dummy = useRef();
    const messagesRef = collection(db, "messages");
    // Query for the 25 more recent messages
    const query = orderBy(messagesRef, 'timestamp').limit(25);

    const [messages] = useCollectionData(query, {idField: 'id'});

    const [formValue, setFormValue] = useState('');

    // Asynchronous function that will send messages using the value of the inputted text
    const sendMsg = async(e) => {
        e.preventDefault();
        const {uid, photoURL} = auth.currentUser;
        await messagesRef.add({
            message: formValue,
            timestamp: serverTimestamp(),
            sender_id: uid,
        })

        setFormValue('');
        dummy.current.scrollIntoView({ behavior: 'smooth '});
    }

    return (
        <>
        <main>
            {/* {messages && messages.map(msg => <ChatMessage key={msg.id} message={msg}/>)} */}
            <span ref={dummy}></span>
        </main>
        <form onSubmit={sendMsg}>
            <input value={formValue} onChange={(e) => setFormValue(e.target.value)} placeholder="hehe"/>
            <button type="submit" disabled={!formValue}>send</button>
        </form>

        </>
    );

}

export default Chatroom;
