import React from 'react';
import { auth, db } from '../firebase';
import { addDoc, collection, serverTimestamp, orderBy, query, limit, where } from "firebase/firestore";

const addUserToFirestore = (user) => {
    const currentUser = auth.currentUser;
    if (currentUser && currentUser.uid) {
      const userRef = db.collection('users').doc(currentUser.uid);
      userRef.set({
        name: currentUser.displayName,
        email: currentUser.email,
        uid: currentUser.uid,
      })
      .then(() => {
        console.log('User data added to Firestore.');
      })
      .catch((error) => {
        console.log('Error addind user data to Firestore:', error);
      });
    } else {
        console.error('User not logged in or missing uid.', currentUser.displayName);
    }
  };
  
  export { addUserToFirestore };
