import React, { useEffect, useRef, useState } from "react";
import { auth, db } from "../firebase";
import { addDoc, getDoc, collection, serverTimestamp, orderBy, query, limit, where } from "firebase/firestore";
import { useCollectionData } from 'react-firebase-hooks/firestore';

// To do: Beautify the chat bubbles, put in CSS for the submission form, add in user PFP

const CreateChatroom = () => {
    const dummy = useRef();

    const [chatrooms, setChatrooms] = useState([]);
    useEffect(() => {
        const fetchChatrooms = async () => {
            const userRef = collection(db, 'users');
            const q = query(userRef, where('uid', '==', auth.currentUser.uid));

            const querySnapshot = await useCollectionData(q);

            if (!querySnapshot.empty) {
                const user = querySnapshot.docs[0].data();
                const userChatrooms = user.chatrooms || [];

                // Set the chatrooms state with the chatrooms from the user's data
                setChatrooms(userChatrooms);
            }
        };
        fetchChatrooms();
    },[]);

    return (
        <div>
            {chatrooms.map((chatroom, index) => (
                <div key={index}>{chatroom.name}</div>
            ))}
        </div>
    );

}

export default CreateChatroom;
