import React, { useEffect, useRef, useState } from "react";
import { auth, db } from "../firebase";
import { getDoc, doc, collection, serverTimestamp, orderBy, query, limit, where } from "firebase/firestore";
import { useCollectionData } from 'react-firebase-hooks/firestore';

// To do: Beautify the chat bubbles, put in CSS for the submission form, add in user PFP

const CreateChatroom = ({ setChatroom }) => {

    const [chatrooms, setChatrooms] = useState([]);

    // Asynchronously gather the chatrooms under the current user
    const getUserChatrooms = async (user) => {
        if (user) {
            if (user[0] && user[0][0]?.chatrooms) {
                setChatrooms(user[0][0].chatrooms);
                console.log("here1");
            }
            else {
                setChatrooms([]);
                console.log("here2");
            }
            console.log(user.data())
        }
        else{
            setChatrooms([]);
            console.log("here3");
        }

    }
    useEffect(() => {
        // Check if the current user is set
        if (auth.currentUser) {
            const userRef = collection(db, "users");
            const q = query(userRef, where("uid", "==", auth.currentUser.uid));
            const user = useCollectionData(q);
            getUserChatrooms(user);
        }

    }, []);



    return (
        <div>
            {chatrooms.map((chatroom, index) => (
                <button key={index} onClick={() => setChatroom(chatroom)}>
                    {chatroom}
                </button>
            ))}
        </div>
    );

}

export default CreateChatroom;
